import{_ as e}from"./chunks/desktop.DXvsyRPb.js";import{_ as o,c as t,o as a,a5 as n}from"./chunks/framework.B9ajF9kL.js";const k=JSON.parse('{"title":"Lingmo OS - Lingmo Operating System","description":"","frontmatter":{"prev":false,"next":false},"headers":[],"relativePath":"Wiki/debian/en/about.md","filePath":"Wiki/debian/en/about.md"}'),i={name:"Wiki/debian/en/about.md"},r=n('<h1 id="lingmo-os-lingmo-operating-system" tabindex="-1">Lingmo OS - Lingmo Operating System <a class="header-anchor" href="#lingmo-os-lingmo-operating-system" aria-label="Permalink to &quot;Lingmo OS - Lingmo Operating System&quot;">​</a></h1><blockquote><p><a href="https://lingmo.org/" target="_blank" rel="noreferrer">LingmoOS Official Website</a></p></blockquote><h3 id="introduction" tabindex="-1">Introduction <a class="header-anchor" href="#introduction" aria-label="Permalink to &quot;Introduction&quot;">​</a></h3><p><img src="'+e+'" alt="LingmoOS"></p><p><code>Lingmo GNU/Linux (Lingmo OS)</code> is a Linux operating system primarily focused on desktop environments. It is developed based on the open-source <code>Linux</code> kernel and is compatible with the <code>amd64</code> architecture. It features an attractive desktop environment, includes software adapted for this desktop environment, and has a unified <code>UI</code> style.</p><p>The goal of <code>Lingmo GNU/Linux</code> is to provide users with a beautiful and practical desktop environment along with a smooth experience, even on low-performance hardware.</p><h3 id="lightweight-smooth-experience" tabindex="-1">Lightweight, Smooth Experience <a class="header-anchor" href="#lightweight-smooth-experience" aria-label="Permalink to &quot;Lightweight, Smooth Experience&quot;">​</a></h3><p><code>Lingmo OS</code> can run on some low-performance hardware. Compared to traditional desktop operating systems, <code>Lingmo OS</code> is more lightweight and smooth.</p><h3 id="ui-new-desktop-environment" tabindex="-1">UI, New Desktop Environment <a class="header-anchor" href="#ui-new-desktop-environment" aria-label="Permalink to &quot;UI, New Desktop Environment&quot;">​</a></h3><p><code>Lingmo OS</code> adopts a new design different from traditional <code>KDE</code>, making operations less complicated. We have also optimized and restructured <code>cutefishDE</code> to make operations more user-friendly and to add more vitality to interactions and visuals.</p><h3 id="built-in-spark-store" tabindex="-1">Built-in Spark Store <a class="header-anchor" href="#built-in-spark-store" aria-label="Permalink to &quot;Built-in Spark Store&quot;">​</a></h3><p>To improve the experience for Linux users, we have integrated the <a href="https://gitee.com/deepin-community-store/spark-store/" target="_blank" rel="noreferrer">Spark Store</a>. Users can download needed software from the store, eliminating the need to use command lines every time to install software. Of course, you can also use the sudo command to install directly.</p><h3 id="reference-documentation" tabindex="-1">Reference Documentation <a class="header-anchor" href="#reference-documentation" aria-label="Permalink to &quot;Reference Documentation&quot;">​</a></h3><blockquote><p><a href="./../en/">Lingmo OS Reference Documentation</a></p></blockquote>',14),s=[r];function d(c,m,l,p,h,u){return a(),t("div",null,s)}const _=o(i,[["render",d]]);export{k as __pageData,_ as default};
