import{_ as e}from"./chunks/desktop.DXvsyRPb.js";import{_ as o,c as a,o as t,a5 as i}from"./chunks/framework.B9ajF9kL.js";const f=JSON.parse('{"title":"Lingmo OS - 灵墨操作系统","description":"","frontmatter":{},"headers":[],"relativePath":"Wiki/debian/zh/about.md","filePath":"Wiki/debian/zh/about.md"}'),r={name:"Wiki/debian/zh/about.md"},n=i('<h1 id="lingmo-os-灵墨操作系统" tabindex="-1">Lingmo OS - 灵墨操作系统 <a class="header-anchor" href="#lingmo-os-灵墨操作系统" aria-label="Permalink to &quot;Lingmo OS - 灵墨操作系统&quot;">​</a></h1><blockquote><p><a href="https://lingmo.org/" target="_blank" rel="noreferrer">LingmoOS官网</a></p></blockquote><h3 id="介绍" tabindex="-1">介绍 <a class="header-anchor" href="#介绍" aria-label="Permalink to &quot;介绍&quot;">​</a></h3><p><img src="'+e+'" alt="LingmoOS"><code>Lingmo GNU/Linux（Lingmo OS）</code>是一个以桌面环境为主的Linux操作系统，它基于开源 <code>Linux</code> 内核开发，适配 <code>amd64</code> 架构。它拥有美观的桌面环境、包含适配该桌面环境的软件、统一的 <code>UI</code> 风格。</p><p><code>Lingmo GNU/Linux</code> 的目标 是让用户在低性能硬件上拥有华丽且实用的桌面环境以及流畅的体验。</p><h3 id="轻量化-流畅体验" tabindex="-1">轻量化，流畅体验 <a class="header-anchor" href="#轻量化-流畅体验" aria-label="Permalink to &quot;轻量化，流畅体验&quot;">​</a></h3><p><code>Lingmo OS</code> 可以在一些低性能硬件上运行，相比传统的桌面操作系统，<code>Lingmo OS</code> 更加轻量、流畅。</p><h3 id="ui、全新的桌面环境" tabindex="-1">UI、全新的桌面环境 <a class="header-anchor" href="#ui、全新的桌面环境" aria-label="Permalink to &quot;UI、全新的桌面环境&quot;">​</a></h3><p><code>Lingmo OS</code> 采取了全新的不同于传统 <code>KDE</code> 的设计，让操作不再繁琐；我们也对 <code>cutefishDE</code> 做了一些优化和重构，使操作更加符合用户，让交互与视觉更加有活力</p><h3 id="内置星火软件商店" tabindex="-1">内置星火软件商店 <a class="header-anchor" href="#内置星火软件商店" aria-label="Permalink to &quot;内置星火软件商店&quot;">​</a></h3><p>我们为了改善 Linux 用户的体验，内置了<a href="https://gitee.com/deepin-community-store/spark-store/" target="_blank" rel="noreferrer">星火软件商店</a>，用户可以在商店内下载所需的软件，使安装软件不用每次都使用命令行。当然，您也可以使用 sudo 指令直接安装。</p><h3 id="参考文档" tabindex="-1">参考文档 <a class="header-anchor" href="#参考文档" aria-label="Permalink to &quot;参考文档&quot;">​</a></h3><blockquote><p><a href="./../zh/">LingmoOS参考文档</a></p></blockquote>',13),c=[n];function d(h,l,s,m,u,_){return t(),a("div",null,c)}const g=o(r,[["render",d]]);export{f as __pageData,g as default};
